# -*- coding: utf-8 -*-
from __future__ import unicode_literals, print_function, absolute_import, division
import os

__all__ = [
    'kitsdir', 'tmpdir', 'logdir', 'cachedir'
]

kitsdir = os.environ.get('KITS')
tmpdir = os.environ.get('KITS_TMP', os.path.join(kitsdir, 'var/tmp'))
logdir = os.environ.get('KITS_LOG', os.path.join(kitsdir, 'var/log'))
cachedir = os.environ.get('KITS_CACHE', os.path.join(kitsdir, 'var/cache'))
rundir = os.environ.get('KITS_RUN', os.path.join(kitsdir, 'var/run'))
